// export const CreateClass =() =>{
//     return <div>Create Class Roster</div>;
// };

import { useState } from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserId";
import { useNavigate } from "react-router-dom";

export const CreateClass =() =>{
    const userID = useGetUserID();

    const [roster, setRoster] = useState({
        studentName: "",
        subjectsEnrolled: [],
        notesOnStudent:"",
        studentPhoto: "",
        absences: 0,
        userOwner: userID,
    }); //state that keeps track of roster we are creating
        //put on inituial structure of how object will look

        const navigate = useNavigate();

        const handleChange = (event)=>{
            const {name, value} = event.target;
            setRoster({...roster,[name]: value});
        };

        const handleEnrolledSubjectChange = (event, idx)=>{
            const {value} = event.target;
            const subjectsEnrolled = roster.subjectsEnrolled;
            subjectsEnrolled[idx] = value;
            setRoster({...roster,subjectsEnrolled});
        };

        const addEnrolledSubject = () => {
            setRoster({...roster, subjectsEnrolled: [...roster.subjectsEnrolled, ""] }) //setting subjectsEnrolled object to be the same as it was before but whatever is after the comma is what id going to change in the object
        };

        const onSubmit= async (event) => {
            event.preventDefault();
            try{
             await axios.post("http://localhost:3001/roster", roster);
             alert("Roster Created!")
             navigate("/class-list");
            }catch(err) {
             console.error(err);

            }
        };
        return (
            <div className="create-class">
                <h2>Create Class Roster</h2> 
                <form onSubmit={onSubmit}>
                    <label htmlFor="studentName">Student Name</label>
                    <input type="text" id="name" name = "name" onChange={handleChange} />
        
                   
                    <label htmlFor="enrolledSubjects">Enrolled Subjects</label>
                      
                      {/* //adding subject and index of that subject */}
                    {roster.enrolledSubjects.map((enrolledSubject, idx) => (
                        <input key={idx} type="text" 
                        name="enrolledSubjects" value={enrolledSubject}
                        onChange={(event) => handleEnrolledSubjectChange(event, idx)}/>
                    ))}
                    <button onClick={addEnrolledSubject}type="button">
                        Add Subject </button>
        
                    <label htmlFor="notesOnStudent">Notes on Student</label>
                    <textarea id="notesOnStudent" name="notesOnStudent" onChange={handleChange}></textarea>
                    <label htmlFor="imageUrl">Image URL</label>
                    <input type="text" id="studentPhoto" name="studentPhoto" onChange={handleChange}/>
                    <label htmlFor="absences">Total Absences</label>
                    <input type="number" id="absences" name="absences" onChange={handleChange}/>
                    
                    <button type="submit">
                        Create Class</button>
                </form>
                </div>
            );
        };
