import React, { useEffect, useState } from "react";
import { useGetUserID } from "../hooks/useGetUserId"
import axios from "axios";
import {useCookies} from 'react-cookie';

export const Home = () => {
  const [roster, setRoster] = useState([]);
  const [savedRoster, setSavedRoster] = useState([]);
  const [cookies,_] = useCookies(["access_token"]);

  const userID = useGetUserID();

  useEffect(() => {
    const fetchRoster = async () => {
      try {
        const response = await axios.get("http://localhost:3001/roster");
        setRoster(response.data);
      } catch (err) {
        console.log(err);
      }
    };

    const fetchSavedRoster = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/recipes/savedRoster/ids/${userID}`
        );
        setSavedRoster(response.data.savedRoster);
      } catch (err) {
        console.log(err);
      }
    };

    fetchRoster();

    if(cookies.access_token)
    fetchSavedRoster();
  }, []);

  const saveRoster = async (rosterID) => {
    try {
      const response = await axios.put("http://localhost:3001/roster", {
        rosterID,
        userID,
      },{headers:{authorization: cookies.access_token}}); //front end authorization for having unverified token
      setSavedRoster(response.data.savedRoster);
    } catch (err) {
      console.log(err);
    }
  };

  const isRosterSaved = (id) => savedRoster.includes(id);

  return (
    <div>
      <h1>Student Profiles</h1>
      <ul>
        {roster.map((rosters) => (
          <li key={rosters._id}>
            <div>
              <h2>{roster.studentName}</h2>
              <button
                onClick={() => saveRoster(roster._id)}
                disabled={isRosterSaved(roster._id)}
              >
                {isRosterSaved(roster._id) ? "Saved" : "Save"}
              </button>
            </div>
            <div className="notesOnStudent">
              <p>{roster.notesOnStudent}</p>
            </div>
            <img src={roster.studentPhoto} alt={roster.studentName} />
            <p>Absences: {roster.absences} days</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

















































