import React, { useEffect, useState } from "react";
import { useGetUserID } from "../hooks/useGetUserId"
import axios from "axios";


export const StudentProfiles = () => {
  const [studentProfiles, setSavedStudentProfiles] = useState([]);

  const userID = useGetUserID();


  useEffect(() => {
    const fetchSavedStudentProfiles = async () => {
      try {
        const response = await axios.post(
          `http://localhost:3001/roster/savedRoster/${userID}`
          
        );

        setSavedStudentProfiles(response.data.savedStudentProfiles);
      } catch (err) {
        console.log(err);
      }
    };

    fetchSavedStudentProfiles();
  }, []);


  return (
    <div>
      <h1>Student Profiles</h1>
      <ul>
        {savedStudentProfiles.map((roster) => (
          <li key={studentProfiles._id}>
            <div>
              <h2>{roster.name}</h2>
            </div>
            <div className="notesOnStudent">
              <p>{roster.notesOnStudent}</p>
            </div>
            <img src={roster.studentPhoto} alt={roster.name} />
            <p>Student Absences: {roster.studentAbsences}days</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export const SavedRoster =() =>{
    return <div>Student Profiles</div>;
 };

